import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Typography, TextField, Button } from "@mui/material";

function Quiz() {
    const [question, setQuestion] = useState("");
    const [answer, setAnswer] = useState("");
    const [userAnswer, setUserAnswer] = useState("");
    const [questionNumber, setQuestionNumber] = useState(1);
    const [score, setScore] = useState(0);

    const navigate = useNavigate()

    const getQuizData = async () => {
        try {
            const response = await axios.post('https://iiofwkaddk.execute-api.us-east-1.amazonaws.com/prod', {"QN": questionNumber});
            const data = response.data;
            setQuestion(data.Question)
            setAnswer(data.Answer)
        } catch(error) {
            console.error(error);
        }
    };

    const handleAnswer = () => {
        let latestQuestionNumber = questionNumber;
        if(userAnswer.toLowerCase() === answer.toLowerCase()) {
            alert("Correct Answer!");
            setQuestionNumber(questionNumber + 1);
            latestQuestionNumber = questionNumber;
            setScore(score + 10);
            if(latestQuestionNumber > 1) {
                navigate("/score", { state: {score} });
            }
        } else {
            alert("Wrong Answer!");
            setScore(score - 10);
        }
        setUserAnswer("")
    }

    useEffect(() => {
        getQuizData();
    }, [questionNumber]);

    return (
        <div className="quiz">
            <form>
                <Typography variant="h5">Question {questionNumber}/10</Typography>
                <br />
                <Typography variant="h4">{question}</Typography>
                <br />
                <br />
                <TextField
                    label="Answer"
                    variant="outlined"
                    value={userAnswer}
                    onChange={(input) => {
                        setUserAnswer(input.target.value)
                    }}
                />
                <br />
                <br />
                <Button variant="contained" color="primary" onClick={handleAnswer}>Send Answer</Button>
            </form>
        </div>
    )
}

export default Quiz
